      // If the char count from s & t is not match, then return false\n            if (table[s.charAt(i)] != table[t.charAt(i) + 128]) {\n                return false;\n            }\n            // Char similarity found (non matching char, but can help to transform to other char)\n            // so just keeping the index reference\n            table[s.charAt(i)] = table[t.charAt(i) + 128] = i + 1;\n        }\n        return true;\n    }\n}\n"))),(0,a.yg)(u,{value:"py",label:"Python",mdxType:"TabItem"},(0,a.yg)(g,{name:"@radojicic23",mdxType:"SolutionAuthor"}),(0,a.yg)("pre",null,(0,a.yg)("code",{parentName:"pre",className:"language-py"},"class Solution:\n    def isIsomorphic(self, s: str, t: str) -> bool:\n        map1 = {}\n        map2 = {}\n        for i in range(len(s)):\n            if s[i] in map1 and map1[s[i]] != t[i]:\n                return False\n            if t[i] in map2 and map2[t[i]] != s[i]:\n                return False\n            map1[s[i]] = t[i]\n            map2[t[i]] = s[i]\n        return True\n"))),(0,a.yg)(u,{value:"js",label:"JavaScript",mdxType:"TabItem"},(0,a.yg)(g,{name:"@radojicic23",mdxType:"SolutionAuthor"}),(0,a.yg)("pre",null,(0,a.yg)("code",{parentName:"pre",className:"language-js"},"/**\n * @param {string} s\n * @param {string} t\n * @return {boolean}\n */\nvar isIsomorphic = function (s, t) {\n  let map1 = {};\n  let map2 = {};\n  for (let i = 0; i < s.length; i++) {\n    if (s[i] in map1 && map1[s[i]] != t[i]) {\n      return false;\n    }\n    if (t[i] in map2 && map2[t[i]] != s[i]) {\n      return false;\n    }\n    map1[s[i]] = t[i];\n    map2[t[i]] = s[i];\n  }\n  return true;\n};\n")))))}y.isMDXComponent=!0},58860(e,n,t){t.d(n,{xA:()=>m,yg:()=>g});var r=t(37953);function a(e,n,t){return n in e?Object.defineProperty(e,n,{value:t,enumerable:!0,configurable:!0,writable:!0}):e[n]=t,e}function i(e,n){var t=Object.keys(e);if(Object.getOwnPropertySymbols){var r=Object.getOwnPropertySymbols(e);n&&(r=r.filter(function(n){return 